% sort2D.mfunction r = sort2D(Cts)    global sbdv        Res = zeros(sbdv, sbdv, 17);    for a = 1:sbdv        for b = 1:sbdv            Res(a,b,:) = modEXP(Cts(a,b).k);        end    end        Errs = zeros(sbdv, sbdv);    for a = 1:sbdv        for b = 1:sbdv            Errs(a,b) = eqmp2D(Res(a,b,:));        end    end        [M, L] = min(Errs);    [N, C] = min(M);    r = [L(C) C];end