# movimento.mclose allclear all% Dados do enunciador = 100;s = 0:0.1:20;v0 = (10 + 0.1 .* 72); % 118029(72)% Equacoes do movimentov = sqrt((8 .* s) - ((0.02 ./ 3) .* power(s, 3)) + power(v0, 2));at = 4 - (0.01 .* power(s, 2));an = power(v, 2) ./ r;a = sqrt(power(at, 2) + power(an, 2));% Mostrando os resultados desejadosfprintf('Grandezas para S=20m\n');fprintf('v(20) = %f m/s\n', v(end));fprintf('a(20) = %f m/s^2\n', a(end));% Graficosfigureplot(s, v, 'b');title('VELOCIDADE x DESLOCAMENTO');xlabel('s (m)');ylabel('v (m/s)');figureplot(s, a, 'r');title('ACELERACAO x DESLOCAMENTO');xlabel('s (m)');ylabel('a (m/s^2)');