function I = gaussLegendre(fun,a,b)  % Quadratura de Gauss-Legendre com 6 pontos  % fun: funcao que avalia vetor  nos = [-0.9324695142031520; -0.6612093864662645;  -0.2386191860831969; 0.2386191860831969;  0.6612093864662645; 0.9324695142031520];  pesos = [ 0.1713244923791703; 0.3607615730481386;  0.4679139345726910; 0.4679139345726910;  0.3607615730481386; 0.1713244923791703];  % mudanca de intervalo de [-1,1] para [a,b]  ab nos = ((b-a)*nos+a+b)/2;  ab pesos = pesos*(b-a)/2;  % aplica regra de Guass-Legendre  I = sum(ab pesos.*fun(ab nos));end