function [n, m, C, coord] = geragrafo(col, lin)  % Base de cálculo  n = col*lin;  m = (col-1)*lin + (lin-1)*col;    % Arestas horizontal  for j=1:lin    for i=1:col-1    k = (j-1)*(col-1) + i;    C(k,1) = (j-1)*col + i;    C(k,2) = (j-1)*col + i+1;    end  end    % Arestas verticais  for i=1:col    for j=1:lin-1    k = (col-1)*lin + (j-1)*col + i;    C(k,1) = i + (j-1)*col;    C(k,2) = i + j*col;    end  end    % Coordenadas para graficar  for i=1:col    for j=1:lin      ig = i + (j-1)*col;      coord(ig,1) = i-1;      coord(ig,2) = j-1;    end  endend